import React, {
  useState,
  useEffect
} from "react";
import {
  Grid,
  Typography,
  Button
} from "@material-ui/core";
import {
  CountdownCircleTimer
} from "react-countdown-circle-timer";

import { DataGrid } from '@material-ui/data-grid';

function GameScreen(props) {
  const { id, count, room, socketIO, username, users, startGame } = props;
  const pageSize = 5;

  console.log("Users");
  console.log(users);

  const columns = [
    {field: 'username', headerName: 'Name', width: 70},
    {field: 'score', headerName: 'Score', width: 70},
    {field: 'timeRemaining', headerName: 'Timer', width: 70}
  ]
  const rows = users.map((user) => {
    console.log(user);

    const row = {
      // username: user.getName(),
      // score: user.getScore(),
      // timeRemaining: user.getTimeRemaining(),
      username: "",
      score: "",
      timeRemaining: ""
    }
    return row;
  })

  const countingButtonPress = (event) => {
    if (startGame) {
      if (event?.keyCode === 32) {
        this.handleButtonPress();
      }
    }
  };

  const handleButtonPress = () => {
    const data = {
      id: id,
      room: room,
      message: "Hello World",
      timestamp: Date.now(),
      username: username,
    };
    console.log(data);
    socketIO.emit("buttonPress", data);
  };

  return (
    <>
      <Grid
        container
        spacing={0}
        align="center"
        justify="center"
        direction="column"
      >
        <Grid container item xs={12} spacing={0}>
          <React.Fragment>
            {/* <DataGrid rows={rows} columns={columns} pageSize={pageSize}/> */}
            {users.map((user) => {
              return (
                <Grid
                  item
                  xs
                  align={"center"}
                  alignItems={"center"}
                  justify={"center"}
                >
                  <CountdownCircleTimer
                    isPlaying
                    duration={10}
                    strokeWidth={5}
                    size={60}
                    colors={[
                      ["#004777", 0.33],
                      ["#F7B801", 0.33],
                      ["#A30000", 0.33],
                    ]}
                  >
                    {({ remainingTime }) => remainingTime}
                  </CountdownCircleTimer>
                  <Typography variant="body">{`${user}`}</Typography>
                </Grid>
              );
            })}
          </React.Fragment>
        </Grid>
        <Grid item xs={12}>
          <Typography>{`Room Id: ${room}`}</Typography>
          <Typography variant="h1">{count}</Typography>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            ref={countingButtonPress}
            onClick={() => {
              handleButtonPress();
            }}
          >
            Press Me
          </Button>
        </Grid>
        <Grid item xs={12}></Grid>
      </Grid>
    </>
  );
}

export default GameScreen;
