import React, { useState, useEffect } from "react";
import {
  Grid,
  Typography,
  Button,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Paper,
} from "@material-ui/core";

import { CountdownCircleTimer } from "react-countdown-circle-timer";

import { DataGrid } from "@material-ui/data-grid";

const styles = {
  root: {
    width: "100%",
    overflowX: "auto",
  },
  table: {
    minWidth: 700,
  },
};

function GameScreen(props) {
  const { id, count, room, socketIO, username, users, startGame } = props;
  const pageSize = 5;

  // useEffect(() => {
  //   document.addEventListener("keydown", countingButtonPress, false);

  //   return () => {
  //     document.removeEventListener("keydown", countingButtonPress, false);
  //   };
  // }, []);

  console.log("Users");
  console.log(users);

  const columns = [
    { field: "id", headerName: "ID", width: 100 },
    { field: "username", headerName: "Name", width: 100 },
    { field: "score", headerName: "Score", width: 100 },
    { field: "timeRemaining", headerName: "Timer", width: 100 },
  ];
  const rows = users.map((user, index) => {
    console.log(user);

    const row = {
      id: index + 1,
      username: user.name,
      score: user.score,
      timeRemaining: user.timeRemaining,
    };
    return row;
  });

  // const countingButtonPress = (event) => {
  //   if (startGame) {
  //     if (event?.keyCode === 32) {
  //       handleButtonPress();
  //     }
  //   }
  // };

  console.log("rows");
  console.log(rows);

  const handleButtonPress = () => {
    const data = {
      id: id,
      room: room,
      message: "Hello World",
      timestamp: Date.now(),
      username: username,
    };

    console.log("data");
    console.log(data);
    socketIO.emit("buttonPress", data);
  };

  return (
    <>
      <Grid
        container
        spacing={0}
        align="center"
        justify="center"
        direction="column"
      >
        <Grid container item xs={12} spacing={0}>
          <React.Fragment>
            <Paper style={styles.root}>
              <Table style={styles.table}>
                <TableHead>
                  <TableRow>
                    <TableCell>Name</TableCell>
                    <TableCell numeric>Score</TableCell>
                    <TableCell>Timer</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.map((user) => {
                    return (
                      <TableRow key={user.id}>
                        <TableCell component="th" scope="row">
                          {user.name}
                        </TableCell>
                        <TableCell numeric>{user.score}</TableCell>
                        <TableCell>{user.timeRemaining}</TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </Paper>
            {/* <DataGrid rows={rows} columns={columns} pageSize={pageSize}/> */}

            {/* {users.map((user) => {
              return (
                <Grid
                  item
                  xs
                  align={"center"}
                  alignItems={"center"}
                  justify={"center"}
                >
                  <CountdownCircleTimer
                    isPlaying
                    duration={10}
                    strokeWidth={5}
                    size={60}
                    colors={[
                      ["#004777", 0.33],
                      ["#F7B801", 0.33],
                      ["#A30000", 0.33],
                    ]}
                  >
                    {({ remainingTime }) => remainingTime}
                  </CountdownCircleTimer>
                  <Typography variant="body">{`${user.name}`}</Typography>
                </Grid>
              );
            })} */}
          </React.Fragment>
        </Grid>
        <Grid item xs={12}>
          <Typography>{`Room Id: ${room}`}</Typography>
          <Typography variant="h1">{count}</Typography>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            // ref={countingButtonPress}
            onClick={() => {
              handleButtonPress();
            }}
          >
            Press Me
          </Button>
        </Grid>
        <Grid item xs={12}></Grid>
      </Grid>
    </>
  );
}

export default GameScreen;
