import React, { useState, useEffect } from "react";
import {
  Grid,
  Typography,
  Button,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableRow,
  Paper,
} from "@material-ui/core";

import { makeStyles } from "@material-ui/core/styles";

import { CountdownCircleTimer } from "react-countdown-circle-timer";

import { DataGrid } from "@material-ui/data-grid";

const styles = {
  root: {
    width: "100%",
    overflowX: "auto",
  },
  table: {
    minWidth: 700,
  },
};

const useStyles = makeStyles((theme) => ({
  root: {
    display: "flex",
    marginTop: theme.spacing.unit * 3,
    overflowX: "hide",
    width: '100%'
  },
  table: {
    minWidth: 340,
  },
  tableCell: {
    paddingRight: 4,
    paddingLeft: 5,
  },
}));

function GameScreen(props) {
  const classes = useStyles();
  const { id, count, room, socketIO, username, users, startGame } = props;
  const pageSize = 5;

  // useEffect(() => {
  //   document.addEventListener("keydown", countingButtonPress, false);

  //   return () => {
  //     document.removeEventListener("keydown", countingButtonPress, false);
  //   };
  // }, []);

  console.log("Users");
  console.log(users);

  const columns = [
    { field: "id", headerName: "ID", width: 100 },
    { field: "username", headerName: "Name", width: 100 },
    { field: "score", headerName: "Score", width: 100 },
    { field: "timeRemaining", headerName: "Timer", width: 100 },
  ];
  const rows = users.map((user, index) => {
    console.log(user);

    const row = {
      id: index + 1,
      username: user.name,
      score: user.score,
      timeRemaining: user.timeRemaining,
    };
    return row;
  });

  // const countingButtonPress = (event) => {
  //   if (startGame) {
  //     if (event?.keyCode === 32) {
  //       handleButtonPress();
  //     }
  //   }
  // };

  console.log("rows");
  console.log(rows);

  const handleButtonPress = () => {
    const data = {
      id: id,
      room: room,
      message: "Hello World",
      timestamp: Date.now(),
      username: username,
    };

    console.log("data");
    console.log(data);
    socketIO.emit("buttonPress", data);
  };

  return (
    <>
      <Grid
        container
        spacing={0}
        align="center"
        justify="center"
        direction="column"
      >
        <Grid container item xs={12} spacing={0}>
          <React.Fragment>
            {/* <Grid container spacing={0} className={classes.root}>
                {users.map((user) => {
                  return (
                    <>
                      <Grid item xs={4}>
                        <Paper className={classes.paper}>{user.name}</Paper>
                      </Grid>
                      <Grid item xs={4}>
                        <Paper className={classes.paper}>{user.score}</Paper>
                      </Grid>
                      <Grid item xs={4}>
                        <Paper className={classes.paper}>
                          <CountdownCircleTimer
                            isPlaying
                            duration={10}
                            strokeWidth={3}
                            size={40}
                            colors={[
                              ["#004777", 0.33],
                              ["#F7B801", 0.33],
                              ["#A30000", 0.33],
                            ]}
                          >
                            {({ remainingTime }) => remainingTime}
                          </CountdownCircleTimer>
                        </Paper>
                      </Grid>
                    </>
                  );
                })}
              </Grid> */}
            <Paper className={classes.root}>
              <Grid item xs={12}>
                <Table className={classes.table}>
                  <TableHead>
                    <TableRow>
                      <TableCell className={classes.tableCell}>Name</TableCell>
                      <TableCell numeric className={classes.tableCell}>Score</TableCell>
                      <TableCell className={classes.tableCell}>Timer</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {users.map((user) => {
                      return (
                        <TableRow key={user.id}>
                          <TableCell component="th" scope="row" className={classes.tableCell}>
                            {user.name}
                          </TableCell>
                          <TableCell numeric className={classes.tableCell}>{user.score}</TableCell>
                          {/* <TableCell>{user.timeRemaining}</TableCell> */}
                          <TableCell className={classes.tableCell}>
                            <CountdownCircleTimer
                              isPlaying
                              duration={10}
                              strokeWidth={3}
                              size={40}
                              colors={[
                                ["#004777", 0.33],
                                ["#F7B801", 0.33],
                                ["#A30000", 0.33],
                              ]}
                            >
                              {({ remainingTime }) => remainingTime}
                            </CountdownCircleTimer>
                          </TableCell>
                        </TableRow>
                      );
                    })}
                  </TableBody>
                </Table>
              </Grid>
            </Paper>
            {/* <DataGrid rows={rows} columns={columns} pageSize={pageSize}/> */}

            {/* {users.map((user) => {
              return (
                <Grid
                  item
                  xs
                  align={"center"}
                  alignItems={"center"}
                  justify={"center"}
                >
                  <CountdownCircleTimer
                    isPlaying
                    duration={10}
                    strokeWidth={5}
                    size={60}
                    colors={[
                      ["#004777", 0.33],
                      ["#F7B801", 0.33],
                      ["#A30000", 0.33],
                    ]}
                  >
                    {({ remainingTime }) => remainingTime}
                  </CountdownCircleTimer>
                  <Typography variant="body">{`${user.name}`}</Typography>
                </Grid>
              );
            })} */}
          </React.Fragment>
        </Grid>
        <Grid item xs={12}>
          <Typography>{`Room Id: ${room}`}</Typography>
          <Typography variant="h1">{count}</Typography>
          <Button
            type="submit"
            variant="contained"
            color="primary"
            // ref={countingButtonPress}
            onClick={() => {
              handleButtonPress();
            }}
          >
            Press Me
          </Button>
        </Grid>
        <Grid item xs={12}></Grid>
      </Grid>
    </>
  );
}

export default GameScreen;
